package com.class5;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		a = a + 100;
		System.out.println(a);

		int c = 100;
		c += 100;
		System.out.println(c);

		c += 35;
		System.out.println(c);

		c += 50;
		System.out.println(c);
	}

}
